rule "convert_timestamp_from_log"
when
  has_field("timestamp_remote")
then
  // the following date format assumes there's no time zone in the string
  let new_date = parse_date(to_string($message.timestamp_remote), "yyyy-MM-dd'T'HH:mm:ss");
  set_field("timestamp", new_date);
  remove_field("timestamp_remote");
end
